import React, { useEffect } from 'react'

import { Helmet } from 'react-helmet'

import './home.css'
import { Button } from 'antd';


const Home = (props) => {

  const [p1p1, setP1p1] = React.useState(0);
  const [p1p2, setP1p2] = React.useState(0);
  const [p1p3, setP1p3] = React.useState(0);
  const [p1p4, setP1p4] = React.useState(0);
  const [p1p5, setP1p5] = React.useState(0);
  const [p1p6, setP1p6] = React.useState(0);
  const [p1BigPit, setP1BigPit] = React.useState(0);
  const [p2p1, setP2p1] = React.useState(0);
  const [p2p2, setP2p2] = React.useState(0);
  const [p2p3, setP2p3] = React.useState(0);
  const [p2p4, setP2p4] = React.useState(0);
  const [p2p5, setP2p5] = React.useState(0);
  const [p2p6, setP2p6] = React.useState(0);
  const [p2BigPit, setP2BigPit] = React.useState(0);
  const [turns, setTurns] = React.useState(0);

  // function for making call to updatePosition of the player and if game is finished update status
  function play(playerTurn,pitIndex) {
    fetch("/updatePosition", {
      method: "PUT",
      body: JSON.stringify({
          name: playerTurn,
          pitIndex: pitIndex,
      }),
      headers: {
          "Content-type": "application/json; charset=UTF-8"
      }}).then(async response => {
      try {
       const data = await response.json()
       setP1p1(data.get(0).getSize());
       setP1p2(data.get(1).getSize());
       setP1p3(data.get(2).getSize());
       setP1p4(data.get(3).getSize());
       setP1p5(data.get(4).getSize());
       setP1p6(data.get(5).getSize());
       setP1BigPit(data.get(6).getSize())
       setP2p1(data.get(7).getSize());
       setP2p2(data.get(8).getSize());
       setP2p3(data.get(9).getSize());
       setP2p4(data.get(10).getSize());
       setP2p5(data.get(11).getSize());
       setP2p6(data.get(12).getSize());
       setP2BigPit(data.get(13).getSize())
        return false;
     } catch(error) {
       console.log('Error happened here!')
     }
    })
  }

  //funtion that triggered by createGame button
  function createGame() {
    fetch("/createGame", {
      method: "POST",
      body: JSON.stringify({
        p1Name: "p1",
        p2Name: "p2"
      }),
      headers: {
          "Content-type": "application/json; charset=UTF-8"
      }}).then(async response => {
      try {
        const data = await response.json();
        setP1p1(data.pits[0].size);
        setP1p2(data.pits[1].size);
        setP1p3(data.pits[2].size);
        setP1p4(data.pits[3].size);
        setP1p5(data.pits[4].size);
        setP1p6(data.pits[5].size);
        setP1BigPit(data.pits[6].size)
        setP2p1(data.pits[7].size);
        setP2p2(data.pits[8].size);
        setP2p3(data.pits[9].size);
        setP2p4(data.pits[10].size);
        setP2p5(data.pits[11].size);
        setP2p6(data.pits[12].size)
        setP2BigPit(data.pits[13].size)

     } catch(error) {
       console.log('Error happened here!')
     }
    })
    setTurns(0);
    const message = " Game Created ";
    document.getElementById("message").textContent=message;
  }
  //creating game board
  return (
    <div className="home-container">
      <Helmet>
        <title>Snakes and Ladders Game</title>
        <meta property="og:title" content="Snakes and Ladders Game" />
      </Helmet>
      <div className="home-containermenu">
        <Button className="button" onClick={createGame}><p>Create Game</p></Button>
        <span id="message" className="alert-message"></span>
      </div>
      <div className="home-container001">
        <div className="home-container002">
          <span className='number'>Player 2 Big Pit</span>
          <div className="player2"><p className='count'>{p2BigPit}</p></div>
        </div>
        <div className="home-container003">
          <span className='number'>99</span>
          <Button className="player2"><p className='count' onClick={() => play('p1',12)}>{p2p6}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>98</span>
          <Button className="player2"><p className='count'>{p2p5}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>97</span>
          <Button className="player2"><p className='count'>{p2p4}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>96</span>
          <Button className="player2"><p className='count'>{p2p3}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>95</span>
          <Button className="player2"><p className='count'>{p2p2}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>94</span>
          <Button className="player2"><p className='count'>{p2p1}</p></Button>
        </div>
        <div className="home-container002">
          <span className='number'>Player 1 Big Pit</span>
          <div className="player1"><p className='count'>{p1BigPit}</p></div>
        </div>
      </div>
      <div className="home-container012">
        <div className="home-container003">
          <span className='number'>81</span>
          <Button className="player1"><p className='count'>{p1p1}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>82</span>
          <Button className="player1"><p className='count'>{p1p2}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>93</span>
          <Button className="player1"><p className='count'>{p1p3}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>92</span>
          <Button className="player1"><p className='count'>{p1p4}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>91</span>
          <Button className="player1"><p className='count'>{p1p5}</p></Button>
        </div>
        <div className="home-container003">
          <span className='number'>93</span>
          <Button className="player1"><p className='count'>{p1p6}</p></Button>
        </div>
      </div>
    </div>
  )
}

export default Home
