import React, { useEffect } from 'react'

import { Helmet } from 'react-helmet'

import './home.css'
import { Button } from 'antd';


const Home = (props) => {

  const [p1p1, setP1p1] = React.useState(0);
  const [p1p2, setP1p2] = React.useState(0);
  const [p1p3, setP1p3] = React.useState(0);
  const [p1p4, setP1p4] = React.useState(0);
  const [p1p5, setP1p5] = React.useState(0);
  const [p1p6, setP1p6] = React.useState(0);
  const [p1BigPit, setP1BigPit] = React.useState(0);
  const [p2p1, setP2p1] = React.useState(0);
  const [p2p2, setP2p2] = React.useState(0);
  const [p2p3, setP2p3] = React.useState(0);
  const [p2p4, setP2p4] = React.useState(0);
  const [p2p5, setP2p5] = React.useState(0);
  const [p2p6, setP2p6] = React.useState(0);
  const [p2BigPit, setP2BigPit] = React.useState(0);
  const [turns, setTurns] = React.useState(0);

  // function for making call to updatePosition of the player and if game is finished update status
  function play(playerTurn,pit,pits) {
    fetch("/updatePosition", {
      method: "PUT",
      body: JSON.stringify({
          name: playerTurn,
          pit: pit,
          pits: pits
      }),
      headers: {
          "Content-type": "application/json; charset=UTF-8"
      }}).then(async response => {
      try {
       const data = await response.json()
       setP1p1(data.get(0).getSize());
       setP1p2(data.get(1).getSize());
       setP1p3(data.get(2).getSize());
       setP1p4(data.get(3).getSize());
       setP1p5(data.get(4).getSize());
       setP1p6(data.get(5).getSize());
       setP1BigPit(data.get(6).getSize())
       setP2p1(data.get(7).getSize());
       setP2p2(data.get(8).getSize());
       setP2p3(data.get(9).getSize());
       setP2p4(data.get(10).getSize());
       setP2p5(data.get(11).getSize());
       setP2p6(data.get(12).getSize());
       setP2BigPit(data.get(13).getSize())

       if(data === 100){ //checking if game is finished
         const message = "Player " + playerTurn+ " won ";
         document.getElementById("message").textContent=message;
         document.getElementById("reset").style.visibility = "hidden";
         updateStatus(playerTurn);
         return true;
        }
        return false;
     } catch(error) {
       console.log('Error happened here!')
     }
